var COLORS_RGBA = new Uint32Array(256);
var COLORS_WEB = [];
for (var i=0; i<256; i++) {
  COLORS_RGBA[i] = rgb2bgr(VIC_NTSC_RGB[i & 15]) | 0xff000000;
  COLORS_WEB[i] = "#"+hex(VIC_NTSC_RGB[i & 15],6);
}

